public class Flywheel extends SubsystemBase {
    /** Velocity setpoints for the flywheel. */
    public enum FlywheelSetpoint {
        Near(RotationsPerSecond.of(40)),
        Far(RotationsPerSecond.of(80));

        /** The velocity target of the setpoint. */
        public final AngularVelocity leaderMotorTarget;

        private FlywheelSetpoint(AngularVelocity leaderMotorTarget) {
            this.leaderMotorTarget = leaderMotorTarget;
        }
    }

    private static final int kNumConfigAttempts = 2;

    private static final double kGearRatio = 1;

    /* leader and follower motors */
    private final CANBus kCANBus = new CANBus("*");
    private final TalonFX leaderMotor = new TalonFX(10, kCANBus);
    private final TalonFX followerMotor = new TalonFX(11, kCANBus);

    /* device status signals */
    private final StatusSignal<AngularVelocity> leaderMotorVelocity = leaderMotor.getVelocity(false);
    private final StatusSignal<Current> leaderMotorTorqueCurrent = leaderMotor.getTorqueCurrent(false);

    /* controls used by the leader motors */
    private final VelocityVoltage setpointRequest = new VelocityVoltage(0);
    private final CoastOut coastRequest = new CoastOut();

    /* simulation */
    private final DCMotor leaderMotorDCMotors = DCMotor.getKrakenX60Foc(2);
    private final LinearSystem<N2, N1, N2> leaderMotorFlywheelSystem = LinearSystemId.createDCMotorSystem(leaderMotorDCMotors, 0.21, kGearRatio);
    private final DCMotorSim leaderMotorFlywheelSim = new DCMotorSim(leaderMotorFlywheelSystem, leaderMotorDCMotors);

    private static final double kSimLoopPeriod = 0.002; // 2 ms
    private Notifier simNotifier = null;
    private double lastSimTime = 0.0;

    /* Mechanism2d visualization for flywheel leaderMotor */
    private final Mechanism2d leaderMotorMech2d = new Mechanism2d(2, 2);
    private final MechanismLigament2d leaderMotorFlywheelMech2d = leaderMotorMech2d.getRoot("Flywheel Root leaderMotor", 1, 1)
        .append(new MechanismLigament2d("Flywheel leaderMotor", 1, 0));

    /** Configs common across all motors. */
    private static final TalonFXConfiguration motorTalonFXInitialConfigs = new TalonFXConfiguration()
        .withMotorOutput(
            new MotorOutputConfigs()
                .withNeutralMode(NeutralModeValue.Brake)
        )
        .withCurrentLimits(
            new CurrentLimitsConfigs()
                .withStatorCurrentLimit(Amps.of(120))
                .withStatorCurrentLimitEnable(true)
        );

    /** Configs for {@link #leaderMotor}. */
    private final TalonFXConfiguration leaderMotorConfigs = motorTalonFXInitialConfigs.clone()
        .withMotorOutput(
            motorTalonFXInitialConfigs.MotorOutput.clone()
                .withInverted(InvertedValue.CounterClockwise_Positive)
        )
        .withFeedback(
            motorTalonFXInitialConfigs.Feedback.clone()
                .withSensorToMechanismRatio(1)
        )
        .withSlot0(
            motorTalonFXInitialConfigs.Slot0.clone()
                .withKP(0)
                .withKI(0)
                .withKD(0)
                .withKS(0)
                .withKV(0.12)
                .withKA(0)
        );

    /** Configs for {@link #followerMotor}. */
    private final TalonFXConfiguration followerMotorConfigs = motorTalonFXInitialConfigs.clone()
        .withMotorOutput(
            motorTalonFXInitialConfigs.MotorOutput.clone()
                .withInverted(InvertedValue.CounterClockwise_Positive)
        );

    public Flywheel() {
        for (int i = 0; i < kNumConfigAttempts; ++i) {
            var status = leaderMotor.getConfigurator().apply(leaderMotorConfigs);
            if (status.isOK()) break;
        }
        for (int i = 0; i < kNumConfigAttempts; ++i) {
            var status = followerMotor.getConfigurator().apply(followerMotorConfigs);
            if (status.isOK()) break;
        }

        followerMotor.setControl(
            new Follower(leaderMotor.getDeviceID(), MotorAlignmentValue.Aligned)
        );

        /* set the default command to neutral output */
        setDefaultCommand(coastFlywheel());

        SmartDashboard.putData("Flywheel leaderMotor", leaderMotorMech2d);

        if (Utils.isSimulation()) {
            startSimThread();
        }
    }

    /**
     * @return The Velocity of the flywheel
     */
    public AngularVelocity getVelocity() {
        return leaderMotorVelocity.getValue();
    }

    /**
     * @return The TorqueCurrent of the flywheel
     */
    public Current getTorqueCurrent() {
        return leaderMotorTorqueCurrent.getValue();
    }

    /**
     * Drives the flywheel to the provided velocity setpoint.
     *
     * @param setpoint Function returning the setpoint to apply
     * @return Command to run
     */
    public Command setTarget(Supplier<FlywheelSetpoint> target) {
        return run(() -> {
            FlywheelSetpoint t = target.get();
            setpointRequest.withVelocity(t.leaderMotorTarget);
            leaderMotor.setControl(setpointRequest);
        });
    }

    /**
     * Stops driving the Flywheel. We use coast so not energy is used during the braking event.
     *
     * @return Command to run
     */
    public Command coastFlywheel() {
        return run(() -> {
            leaderMotor.setControl(coastRequest);
        });
    }

    @Override
    public void periodic() {
        /* refresh all status signals */
        BaseStatusSignal.refreshAll(
            leaderMotorVelocity,
            leaderMotorTorqueCurrent
        );

        leaderMotorFlywheelMech2d.setLength(
            leaderMotorVelocity.getValueAsDouble() / 100.0
        );
    }

    private void startSimThread() {
        leaderMotor.getSimState().Orientation = ChassisReference.CounterClockwise_Positive;
        leaderMotor.getSimState().setMotorType(TalonFXSimState.MotorType.KrakenX60);
        followerMotor.getSimState().Orientation = ChassisReference.CounterClockwise_Positive;
        followerMotor.getSimState().setMotorType(TalonFXSimState.MotorType.KrakenX60);

        lastSimTime = Utils.getCurrentTimeSeconds();

        /* Run simulation at a faster rate so PID gains behave more reasonably */
        simNotifier = new Notifier(() -> {
            /* Calculate the time delta */
            final double currentTime = Utils.getCurrentTimeSeconds();
            final double deltaTime = currentTime - lastSimTime;
            lastSimTime = currentTime;

            final var leaderMotorSim = leaderMotor.getSimState();
            final var followerMotorSim = followerMotor.getSimState();

            /* First set the supply voltage of all the devices */
            leaderMotorSim.setSupplyVoltage(RobotController.getBatteryVoltage());
            followerMotorSim.setSupplyVoltage(RobotController.getBatteryVoltage());

            /* Then calculate the new velocity of the simulated flywheel */
            leaderMotorFlywheelSim.setInputVoltage(leaderMotorSim.getMotorVoltage());
            leaderMotorFlywheelSim.update(deltaTime);

            /* Apply the new rotor velocity to the motors (before gear ratio) */
            leaderMotorSim.setRawRotorPosition(
                Radians.of(leaderMotorFlywheelSim.getAngularPositionRad() * kGearRatio)
            );
            leaderMotorSim.setRotorVelocity(
                RadiansPerSecond.of(leaderMotorFlywheelSim.getAngularVelocityRadPerSec() * kGearRatio)
            );
            followerMotorSim.setRawRotorPosition(
                Radians.of(leaderMotorFlywheelSim.getAngularPositionRad() * kGearRatio)
            );
            followerMotorSim.setRotorVelocity(
                RadiansPerSecond.of(leaderMotorFlywheelSim.getAngularVelocityRadPerSec() * kGearRatio)
            );
        });
        simNotifier.startPeriodic(kSimLoopPeriod);
    }
}